// Deps
import React, { useState, useEffect, useRef, createRef } from 'react'
import { useTranslation } from 'react-i18next' 
import './header.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser, faExclamationTriangle, faInfoCircle } from '@fortawesome/free-solid-svg-icons'

// Components
import LangSelector from "./LangSelector"
import streamHubLogo from "../StreamHub_Logo.png"
import Search from "./Search"
import AddEventModal from "./AddEventModal"
import LoginModal from "./LoginModal"
import UserProfile from "./UserProfile"


function Header(props){

    const [showAddEvent, setShowAddEvent] = useState(false)
    const [showMustLogin, setShowMustLogin] = useState(false)
    const [showLogin, setShowLogin] = useState(false)
    const [showProfile, SetShowProfile] = useState(false)

    const { t } = useTranslation()

    const handleLogoClick = () => {
        window.location.reload()
    }

    const handleShowAddEvent = () => {
        if(props.isLoggedIn){
            setShowAddEvent(true)
        } else {
            setShowMustLogin(true)
            setTimeout(() => {setShowMustLogin(false)
            }, 2000)
        }
    }

    const handleHideAddEvent = () => {
        setShowAddEvent(false)
    }

    const handleShowLogin = () => {
        setShowLogin(true)
    }

    const handleHideLogin = () => {
        setShowLogin(false)
    }

    const handleShowProfile = () => {
        showProfile ? SetShowProfile(false) : SetShowProfile(true)
        console.log("userData:",props.userData)
    }

    const handleHideProfile = () => {
        SetShowProfile(false)
    }

    const savedCount = props.userData.savedItems && props.userData.savedItems.split(',').length-1

    const loginButton = !props.isLoggedIn ? <button 
                                            className="header-login-button" 
                                            onClick={handleShowLogin}>
                                                {t("Login")}
                                            </button> : null
    const profileNotification = props.userData.savedItems ? <div 
                                                            className="header-profile-button-notifications">
                                                                {savedCount}
                                                            </div> : null
    const profileButton = props.isLoggedIn ? <button 
                                            className="header-profile-button" 
                                            onClick={handleShowProfile}>
                                                <FontAwesomeIcon icon={faUser} size="lg"/>
                                            </button> : null                                  

    const userProfile = props.isLoggedIn && showProfile ?   <UserProfile 
                                        handleHideProfile={handleHideProfile}
                                        showProfile={showProfile}
                                        setIsLoggedIn={props.setIsLoggedIn}
                                        setUserData={props.setUserData}
                                        userData={props.userData}
                                        feed={props.feed}
                                        isLoggedIn={props.isLoggedIn}
                                        setUserData={props.setUserData}
                                        userData={props.userData}
                                        /> : null

    return (
        <div className="header-container">

            <img 
                className="header-logo" 
                src={streamHubLogo} 
                alt="StreamHub Logo"  
                onClick={handleLogoClick}
            />

            <Search 
                setSearchFilter={props.setSearchFilter}
                searchFilter={props.searchFilter}
            />
            <div className="right-header-container">
                <LangSelector 
                    language={props.language} 
                    changeLanguage={props.changeLanguage}
                />
                <div style={{position: "relative"}}>
                <button 
                    className="header-add-event-button" 
                    onClick={handleShowAddEvent}>
                    + {t("Add Event")}
                </button>
                {showMustLogin ? <div className={showMustLogin ? "add-event-dropdown-alert-out" : "add-event-dropdown-alert-in"}>
                                    <FontAwesomeIcon icon={faInfoCircle} size="1x"/>
                                    <p>{t("Login Required!")}</p>
                                 </div> : null}
                </div>
                <div className="header-user-container">
                    {loginButton}
                    {profileNotification}
                    {profileButton}
                    {userProfile}
                </div>                
            </div>

            {showAddEvent ? <AddEventModal 
                             handleHideAddEvent={handleHideAddEvent}
                             /> : null}

            {showLogin ? <LoginModal 
                         handleHideLogin={handleHideLogin} 
                         setIsLoggedIn={props.setIsLoggedIn} 
                         isLoggedIn={props.isLoggedIn}
                         setUserData={props.setUserData}
                         /> : null}

        </div>
    )
}

export default Header;


