import React from "react"
import "./share-panel.css"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faLink, faEnvelope } from "@fortawesome/free-solid-svg-icons"
import { faWhatsapp } from "@fortawesome/free-brands-svg-icons"


function Footer(props) {

    const handleEmail = () => {
        window.location = `mailto:roigfrey@gmail.com
        ?subject=streamHub: I would like to report Item #${this.props.feed.itemID}
        &body=I would like to report the event "${this.props.feed.itemTitle}".`
    }

    const handleFacebook = () => {
        const shareURL = `https://www.facebook.com/sharer/sharer.php?u=${props.item.itemURL}`
        window.open(shareURL, '_blank', 'location=yes,height=570,width=520,scrollbars=yes,status=yes')
    }

    const handleTwitter = () => {
        const shareURL = `https://www.facebook.com/sharer/sharer.php?u=${this.props.feed.itemURL}`
        window.open(shareURL, '_blank', 'location=yes,height=570,width=520,scrollbars=yes,status=yes')
    }

    const handleWhatsapp = () => {
        const shareURL = `whatsapp://send?text= Check out this event I found using Stream-hub.net %0D%0A${props.item.itemURL}`
        window.open(shareURL, '_blank', 'location=yes,height=570,width=520,scrollbars=yes,status=yes')
    }

    const handleCopyLink = () => {

    }




    return(
        <div className="share-panel-container">

            {/* <!-- Email --> */}
            <div onClick={handleEmail} style={{background: "rgb(216, 17, 60)"}}>
                <FontAwesomeIcon icon={faEnvelope} style={{color: "white"}} />
            </div>

            {/* <!-- Facebook --> */}
            <div onClick={handleFacebook}>
                <img src="https://simplesharebuttons.com/images/somacro/facebook.png" alt="Facebook" />
            </div>

            {/* <!-- Twitter --> */}
            <a href="https://twitter.com/share?url=https://simplesharebuttons.com&amp;text=Simple%20Share%20Buttons&amp;hashtags=simplesharebuttons" target="_blank">
                <img src="https://simplesharebuttons.com/images/somacro/twitter.png" alt="Twitter" />
            </a>

            {/* <!-- Whatsapp --> */}
            <div style={{background: "#24d364"}} onClick={handleWhatsapp}>
                <FontAwesomeIcon icon={faWhatsapp} style={{color: "white"}} />
            </div>

            {/* <!-- Copy Link --> */}
            <div>
                <FontAwesomeIcon icon={faLink} style={{color: "white"}} />
            </div>

        </div>
    )
}

export default Footer
