import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";

i18n.use(LanguageDetector).init({
  // we init with resources
  resources: {
    en: {
      translations: {
        "Add Event":"1",
        "Login":"2",
        "Select Topic":"3",
        "Kids":"4",
        "Lectures":"5",
        "Fitness":"6",
        "Fun":"7",
        "Clear":"8",
        "Select Date":"9",
        "Today":"10",
        "WeekDay Array":"11",
        "Search Results":"12",
        "Results":"13",
        "Link to Live Event":"14",
        "Save":"15",
        "Share":"16",
        "View Host":"17",
        "Report":"18",
        "Views":"19",
        "":""
      }
    },
    he: {
      translations: {
        "Add Event":"הוסף אירוע",
      }
    }
  },
  fallbackLng: "en",
  debug: true,

  // have a common namespace used around the full app
  ns: ["translations"],
  defaultNS: "translations",

  keySeparator: false, // Content used in place of keys

  interpolation: {
    escapeValue: false, // not needed for react!!
    formatSeparator: ","
  },

  react: {
    wait: true
  }
});

export default i18n;
