import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLanguage } from '@fortawesome/free-solid-svg-icons'
import "./lang-selector.css"

function LangSelector(props){

    const [showMenu, setShowMenu] = useState(false)
    const [dropDownValue, setDropDownValue] = useState(false)

    useEffect(() => {
        if (!dropDownValue){
            if(props.language === "en"){
                setDropDownValue("English")
            } else if(props.language === "he"){
                setDropDownValue("Hebrew")
            }
        }
      })


    const handleShowMenu = (e) => {
        console.log("Hello World")
    }

    return(
        <div className="lang-dropdown">
            <FontAwesomeIcon className="lang-dropdown-icon" icon={faLanguage} size="2x" color="white" />
            <div>
            <button 
            className="lang-dropdown-button" 
            onClick={() => {console.log("Test")}}>
                {dropDownValue}
            </button>
            {showMenu ? <div className="lang-dropdown-menu">
                <button>EN</button>
                <button>HE</button>
            </div> : null}
            </div>
            {/* <select className="lang-dropdown-menu">
                <option value="en" onClick={() => props.changeLanguage("en")}>English</option>
                <option value="he" onClick={() => props.changeLanguage("he")}>Hebrew</option>
            </select> */}
        </div>
    )
}

export default LangSelector
