import React, { useState, useEffect } from 'react'
import "./user-profile.css"
import * as firebase from "firebase/app";
import "firebase/auth"
import fire from "../fire"; //required!

function UserProfile(props){

    useEffect(() => {
        if(props.showProfile){
            document.addEventListener('click', handleCloseMenu)
        }
    })

    const handleCloseMenu = (event) => {
        if(event.target === event.currentTarget){
            props.handleHideProfile()
            document.removeEventListener('click', handleCloseMenu)
        }
    }

    const handleSignOut = () => {
        console.log("userPull:",firebase.user())
        // firebase
        // .auth()
        // .signOut()
        // .then(props.setIsLoggedIn(false))
    }


    return(
        <div className="user-profile-dropdown">
            <div className="user-profile-container-grid">
                <div className="user-profile-header">
                    <h2>Hello {firebase.auth().user}</h2>
                </div>
                <div className="user-profile-subtitle">

                </div>
                <div className="user-profile-saved-feed">

                </div>
                <div className="user-profile-footer">
                    <button type="button" className="user-profile-logout-button" onClick={handleSignOut}>Logout</button>
                </div>
            </div>
        </div>
    )
}

export default UserProfile
