import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLanguage } from '@fortawesome/free-solid-svg-icons'
import "./lang-selector.css"

function LangSelector(props){

    const [showMenu, setShowMenu] = useState(false)
    const [dropDownValue, setDropDownValue] = useState(false)

    useEffect(() => {
        if (!dropDownValue){
            if(props.language === "en"){
                setDropDownValue("English")
            } else if(props.language === "he"){
                setDropDownValue("Hebrew")
            }
        }
      })


    const handleShowMenu = () => {
        setShowMenu(true)
        document.addEventListener('click', handleCloseMenu)
    }

    const handleCloseMenu = (e) => {
        // if (!this.dropdownMenu.contains(e.target)) {
        //     setShowMenu(false)
        //     document.removeEventListener('click', handleCloseMenu)
        // }
        setShowMenu(false)
            document.removeEventListener('click', handleCloseMenu)
    }

    return(
        <div className="lang-dropdown">
            <FontAwesomeIcon className="lang-dropdown-icon" icon={faLanguage} size="2x" color="white" />

            <div >
            <button 
            className="lang-dropdown-button" 
            onClick={() => handleShowMenu}>
                {dropDownValue}
            </button>
            {showMenu ? 
            <div 
            className="lang-dropdown-menu"
            ref={(element) => {
                this.dropdownMenu = element
            }}>
                <button onClick={() => props.changeLanguage("en")}>EN</button>
                <button onClick={() => props.changeLanguage("he")}>HE</button>
            </div> : null}
            </div>
        </div>
    )
}

export default LangSelector
