import React, { useState, useEffect } from "react"
import "./login-modal.css"
import { useTranslation } from 'react-i18next'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { faTwitter, faFacebookF, faGoogle } from "@fortawesome/free-brands-svg-icons"

function LoginModal(props){

    const { t } = useTranslation()

    const handleClose = (event) => {
        if(event.target === event.currentTarget) 
        props.handleHideLogin()
    }

    const handleLogin = (event) => {
        const { name } = event.target
        if(name === "facebook") {
            props.setIsLoggedIn("facebook")
        } else if(name === "twitter") {
            props.setIsLoggedIn("twitter")
        } else if(name === "google") {
            props.setIsLoggedIn("google")
        }
        console.log("logged in?",props.isLoggedIn)
        console.log("name:",name)
    }

    return(
        <div className="modal-container-login" name="mainmodal" onClick={handleClose}>
            <div className="modal-container-window-parent-login">
                <form className="modal-container-window-login">
                    <div className="modal-close-button-login" onClick={props.handleHideLogin}>
                        <FontAwesomeIcon icon={faTimes} size="1x"/>
                    </div>
                    <div className="modal-form-container-login">
                        <button 
                        type="button"
                        name="facebook" 
                        className="Social-login-button-facebook"
                        onClick={handleLogin}
                        >
                            <FontAwesomeIcon icon={faFacebookF} size="lg"/>
                            <p style={{paddingRight:40}}>Login with Facebook</p>
                        </button>
                        <button 
                        type="button"
                        name="twitter" 
                        className="Social-login-button-twitter"
                        onClick={handleLogin}
                        >
                            <FontAwesomeIcon icon={faTwitter} size="lg"/>
                            <p style={{paddingRight:40}}>Login with Twitter</p>
                        </button>
                        <button 
                        type="button"
                        name="google" 
                        className="Social-login-button-google"
                        onClick={handleLogin}
                        >
                            <FontAwesomeIcon icon={faGoogle} size="lg"/>
                            <p style={{paddingRight:40}}>Login with Google</p>
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default LoginModal
